var open=document.querySelector(".btn-contacts"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".btn-close"),user_name=popup.querySelector("[name=name]"),overlay=document.querySelector(".modal-overlay");open.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-content-show"),overlay.classList.add("modal-overlay-show"),user_name.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-content-show"),overlay.classList.remove("modal-overlay-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),overlay.classList.remove("modal-overlay-show"))}),overlay.addEventListener("click",function(e){e.preventDefault(),popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),overlay.classList.remove("modal-overlay-show"))});